pragma solidity 0.8.10;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract p2ploan{
using SafeERC20 for IERC20;
IERC20 public a2z;

 enum Status { initial, lent, paid, destroyed }
           Status status;
           Status constant defaultStatus = Status.initial;

struct Loan {
Status status;
address  loanToken;
address  collateralToken;
address  lender;
address  borrower;
uint256  amount;
uint256  collateralValue;
uint256  payoffAmount;
uint256  dueDate;
uint256  interestRate;
uint256  LTV;
uint256  expirationRequest;
}
///mapping(uint256 => Loan) public loanLookup;
 Loan[] private loans;


constructor(IERC20 _a2z){
    a2z = _a2z;
   
}

function requestLoan(address _borrower, address _loanToken, address _collateralToken, uint256 _amount, uint256 _collateralValue,  uint256 _payOffAmount,uint256 _dueDate, uint256 _interestRate, uint256 _LTV, uint256 _expirationRequest)public returns(uint256) {
    require(_borrower != address(0));
    require(_amount != 0);
    require(_interestRate != 0); 
    require(_dueDate != 0);
    require(_expirationRequest > block.timestamp);

 Loan memory Id = Loan (
         Status.initial,
        _loanToken,
        _collateralToken,
         address(0),
        _borrower,
        _amount,
        _collateralValue,
        _payOffAmount,
        _dueDate,
        _interestRate,
        _LTV,
        _expirationRequest
 );

 /// uint256 id = loans.push(id) -1;
       loans.push(Id);
       uint256 id = loans.length - 1;

          if (msg.sender == _borrower) {
            approveLoan(id); 
        }

        return id;
    }


     function approveLoan(uint256 id) public view returns(bool) {
     Loan memory loan = loans[id];
     require(loan.status == Status.initial);
     return true;
     
  }













}
